import Header from './Header'
import Body from './body'

const debouncing = (func,delay) => {
  let interval;
  return ((...args) => {
    const context = this;
    clearTimeout(interval);
    interval = setTimeout(() => {
      func.apply(context,args)
    }, delay);
  })
}

const getData = (search,setData) => {
