import React,{useState, useEffect} from 'react';
import {GridList,GridListTile,ListSubheader,GridListTileBar,Modal,SimpleModal,makeStyles,Paper} from '@material-ui/core'
import './body.css';


const handleClick = (setOpen,url,title,setModal) => {
    setOpen(true);
    setModal({url:url,title:title})
}

const handleLoadMore = (page,setPage) => {
  setPage(page+1);
}

const useStyles = makeStyles({
    root: {
        position: 'fixed',
        alignSelf:'center',
        top: '20%',
        // bottom: '50%',
        left: '30%',
        // right: '50%',
        // margin: 'auto'
    },
  });

function Body(props) {
    const [open,setOpen] = useState(false);
    const [modal,setModal] = useState({});
console.log(props.data);
  useEffect(() => {
    document.getElementsByClassName('photos').addEventListener("scroll", ()=>console.log('scroll'), false)
    // document.addEventListener("scroll", ()=>console.log('scroll'), false)
  },[])

      const classes = useStyles(props);
  return (
      <div className="photos" onScroll={()=>console.log('scroll')} >
      <GridList cellHeight={250} cols={5} spacing={8} >
        {props.data.map(val => {
            let url = `https://farm${val.farm}.staticflickr.com/${val.server}/${val.id}_${val.secret}.jpg`;
            return(
          <GridListTile key={val.id} onClick={() => handleClick(setOpen,url,val.title,setModal)}>
            <img src={url} />
          </GridListTile>       
        )})}
      </GridList>
      <Modal
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
        open={open}
        onClose={() => setOpen(false)}
      >
          <Paper className={classes.root}>
        <GridListTile >
            <img src={modal.url} style={{maxHeight:'500px'}}/>
            <GridListTileBar
              title={modal.title}
            />
        </GridListTile>
        </Paper>
      </Modal>
      <button onClick={() => handleLoadMore(props.page,props.setPage)}>load more</button>
  </div>
  );
}

export default Body;
