import React,{useState} from 'react';
import {GridList,GridListTile,ListSubheader,GridListTileBar,Modal,SimpleModal,makeStyles} from '@material-ui/core'

const handleClick = (setOpen,url,title,setModal) => {
    setOpen(true);
    setModal({url:url,title:title})
}

const useStyles = makeStyles({
    root: {
        top:'50px',
        left: '50%'
    },
  });

function Body(props) {
    const [open,setOpen] = useState(false);
    const [modal,setModal] = useState({});


      const classes = useStyles(props);
  return (
      <div>
      <GridList cellHeight={250} cols={4} spacing={8}>
        {props.data.map(val => {
            let url = `https://farm${val.farm}.staticflickr.com/${val.server}/${val.id}_${val.secret}.jpg`;
            return(
          <GridListTile key={val.id} onClick={() => handleClick(setOpen,url,val.title,setModal)}>
            <img src={url} />
          </GridListTile>       
        )})}
      </GridList>
      <Modal
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
        open={open}
        onClose={() => setOpen(false)}
      >
        <div style={{width:'500px'}}>
        <GridListTile className={classes.root}>
            <img src={modal.url} />
            <GridListTileBar
              title={modal.title}
            />
        </GridListTile>
        </div>
      </Modal>

  </div>
  );
}

export default Body;
