import React from 'react';
import {Autocomplete} from '@material-ui/lab'
import {TextField,makeStyles} from '@material-ui/core'
import './Header.css';

const useStyles = makeStyles({
    root: {
       width:'300px',
       backgroundColor:'white',
    },
    text: {
        margin:'0px'
    }
});

function Header(props) {
    const searches = localStorage.getItem('search').split(',');
    let classes = useStyles(props);

    saveSearch = (e, search) => {
      if(13 == e.which && !searches.includes(search)) {
        localStorage.setItem('search',searches.join(',').concat(`,${e.target.value}`))
      }
    }
  return (
  <div className='header'>
      <div className='search'>
          <span className='span'>Search Ptotos</span>
    <Autocomplete
        className={classes.root}
        freeSolo
        disableClearable
        onInputChange = {(e,value) => props.setQuery(value)}
        options={query.map(option => option)}
        renderInput={params => (
          <TextField
          className={classes.text}
            {...params}
            margin="normal"
            onChange = { (e) => props.setQuery(e.target.value)}
            onKeyPress={(e)=> saveSearch(e)}
            fullWidth
            InputProps={{ ...params.InputProps, type: 'search' }}
          />
        )}
      />
      </div>
  </div>
  );
}

export default Header;
