import React,{} from 'react';
import {Autocomplete} from '@material-ui/lab'
import {TextField,makeStyles} from '@material-ui/core'
import './Header.css';

const useStyles = makeStyles({
    root: {
       position:'absolute',
       width:'400px',
       backgroundColor:'white',
       left:'300px',
       top:'20px'
    },
    text: {
        margin:'0px'
    }
  });
function Header(props) {
    const query = localStorage.getItem('search').split(',');
    let classes = useStyles(props);
  return (
  <div className='header'>
    <Autocomplete
        className={classes.root}
        freeSolo
        id="free-solo-2-demo"
        disableClearable
        onInputChange = {(e,value) => props.setQuery(value)}
        options={query.map(option => option)}
        renderInput={params => (
          <TextField
          className={classes.text}
            {...params}
            margin="normal"
            onChange = { (e) => {console.log(e);props.setQuery(e.target.value)}}
            onKeyPress={(e)=> {if(e.which==13) {localStorage.setItem('search',query.join(',').concat(`,${e.target.value}`))}}}
            fullWidth
            InputProps={{ ...params.InputProps, type: 'search' }}
          />
        )}
      />
  </div>
  );
}

export default Header;
