import React,{useState} from 'react';
import {GridList,GridListTile,ListSubheader,GridListTileBar,Modal,SimpleModal} from '@material-ui/core'

const handleClick = (setOpen,url,title,setModal) => {
    setOpen(true);
    setModal({url:url,})
}

function Body(props) {
    const [open,setOpen] = useState(false);
    const [modal,setModal] = useState({});
  return (
      <div>
      <GridList cellHeight={250} cols={4} spacing={8}>
        {props.data.map(val => {
            let url = `https://farm${val.farm}.staticflickr.com/${val.server}/${val.id}_${val.secret}.jpg`;
            return(
          <GridListTile key={val.id} onClick={() => handleClick(setOpen,url,val.title,setModal)}>
            <img src={url} />
          </GridListTile>       
        )})}
      </GridList>
      <Modal
        aria-labelledby="simple-modal-title"
        aria-describedby="simple-modal-description"
        open={open}
        onClose={() => setOpen(false)}
      >
        <div >
         <img src="https://farm66.staticflickr.com/65535/49566481847_ef9dac5acb.jpg" />
        </div>
      </Modal>
  </div>
  );
}

export default Body;
