import React,{useState, useEffect} from 'react';
import ImageModal from './ImageModal'
import _ from 'lodash';
import './body.css';

const handleClick = (setOpen,url,title,setModal) => {
    setOpen(true);
    setModal({url:url,title:title})
}


const getData = (search,setData,data,page) => {
  const url = `https://www.flickr.com/services/rest/?method=flickr.photos.search&per_page=25&api_key=506e716ab594396861cd8e374cf41d89&text=${search}&page=${page}&format=json&nojsoncallback=1`;
  fetch(url)
  .then((response) => response.json())
  .then(value => {
    if('photos' in value) {
    setData([...data,...value.photos.photo])
      console.log(url,page);
  }
  })
  console.log(search);
}

var debouneGetData;

function Body(props) {
    const [open,setOpen] = useState(false);
    const [modal,setModal] = useState({});
    const [data,setData] = useState([]);
    const [page,setPage] = useState(1);

    const classes = useStyles(props);

    useEffect(() => {
      debouneGetData = _.debounce(getData,2000);
      window.addEventListener("scroll", handleScroll, false)
      console.log('listner',page)
      return (()=> {console.log('we unmount')});
        window.removeEventListener("scroll",handleScroll);
    },[]);

    useEffect(() => {
      debouneGetData(props.query,setData,data,page);
      console.log('changes')
    },[props.query,page])


    const handleScroll = () => {
      let element = document.getElementById('photos');
      console.log(window.scrollY, window.innerHeight, element.clientHeight, element.offsetTop)
      if (window.scrollY + window.innerHeight > element.clientHeight + element.offsetTop) {
        setPage( page => page + 1 );
        console.log('scroll',data);
      }
    }


  const getData = (search,setData,data,page) => {
    const url = `https://www.flickr.com/services/rest/?method=flickr.photos.search&per_page=25&api_key=506e716ab594396861cd8e374cf41d89&text=${search}&page=${page}&format=json&nojsoncallback=1`;
    fetch(url)
    .then((response) => response.json())
    .then(value => {
      if('photos' in value) {
      setData([...data,...value.photos.photo])
        console.log(url,page);
    }
    })
    console.log(search);
  }

  return (
      <div className="photos" id='photos'  >      
        {data.map(val => {
            let url = `https://farm${val.farm}.staticflickr.com/${val.server}/${val.id}_${val.secret}.jpg`;
            return(
            <img src={url} className="list_image" key={val.id} onClick={() => handleClick(setOpen,url,val.title,setModal)}/>
        )})}
      <ImageModal image={modal} open={open} setOpen={setOpen}/>
  </div>
  );
}

export default Body;
