import React,{useState, useEffect} from 'react';
import Header from './Header'
import Body from './body'

const debouncing = (func,delay) => {
  let interval;
  return ((...args) => {
    const context = this;
    clearTimeout(interval);
    interval = setTimeout(() => {
      func(...args);
    }, delay);
  })
}

var f;

const getData = (search,setData) => {
  const url = `https://www.flickr.com/services/rest/?method=flickr.photos.search&api_key=4cb72a9e20f4fa05cfd6e99b8fd5b92a&text=${search}&per_page=100&format=json&nojsoncallback=1`;
  fetch(url)
  .then((response) => response.json())
  .then(value => setData(value));
  // console.log(search);
}

function App() {
  const [query,setQuery] = useState('');
  const [data,setData] = useState([]);

  useEffect(() => {
    f = debouncing(getData,5000);
  },[]);

  useEffect(() => {
    f(query,setData);
  },[query]);

  console.log(data);
  return (
  <div>
    <Header query={query} setQuery={setQuery}></Header>
    <Body qry={query}></Body>
  </div>
  );
}

export default App;
